<script type="text/javascript">    function SendpagingBindRequest(pagingurldata, urls) {        var paging_url = pagingurldata;        $.ajax({            type: "GET",            url: urls,            data: paging_url + "&ajax=Y",            async: true,            success: function (result) {                $("html, body").animate({                    scrollTop: $("#gridbody_front").offset().top                }, 800);                $("#gridbody_front").replaceWith(result);                //                UnSetBackGround();            }        });    }    function SetBackGround()    {        $("#dvprocessing").show();    }    function UnSetBackGround()    {        document.getElementById('dimmer').style.width = 110;        document.getElementById('dimmer').style.height = 0;        document.getElementById('dimmer').style.visibility = "";        document.getElementById('dvprocessing').style.display = 'none';    }</script><div id="gridbody_front">    <<main class="page-content section-98 section-sm-124">        <!-- Section Blog Grid 3 Columns-->        <section>            <div class="shell-fluid">                <div class="range range-sm-center">                    <div class="col-sm-10 col-md-12">                        <div class="range">                            <?php                            $i = 1;                            foreach ($ShowAllPagesRecords as $row) {                                ?>                                <div class="cell-md-6 cell-lg-4">                                    <!-- Post Boxed--><a class="reveal-block" href="<?php echo SITE_PATH . $row['varAlias']; ?>">                                        <div class="post post-boxed"  style="box-shadow:0 16px 38px -12px rgba(0, 0, 0, 0.56), 0 4px 25px 0 rgba(0, 0, 0, 0.12), 0 8px 10px -5px rgba(0, 0, 0, 0.2)">                                            <!-- Post media-->                                            <?php                                            $imagename = $row['varImage'];                                            $imagepath = 'upimages/blogs/images/' . $imagename;                                            if (file_exists($imagepath) && $row['varImage'] != '') {                                                $image_thumb1 = image_thumb($imagepath, BLOGS_WIDTH, BLOGS_HEIGHT);                                            }                                            if ($imagename != "") {                                                if (file_exists($imagepath)) {                                                    $image = $image_thumb1;                                                } else {                                                    $image = FRONT_MEDIA_URL . "images/blog/post-01-570x321.jpg";                                                }                                            } else {                                                $image = FRONT_MEDIA_URL . "images/blog/post-01-570x321.jpg";                                            }                                            ?>                                            <header class="post-media"><img class="img-responsive" width="570" height="321" src="<?php echo $image; ?>" alt=""></header>                                            <!-- Post content-->                                            <section class="post-content text-left">                                                <div class="post-tags group-xs">                                                    <?php                                                    $type = $row['varType'];                                                    $atype = explode(",", $type);                                                    foreach ($atype as $bg) {                                                        ?>                                                        <span class="label-custom label-lg-custom label-primary"><?php echo $bg; 